import styled, { createGlobalStyle, ThemeProvider } from "styled-components";
import { useState } from "react";
import Router from "./Router";

import "./App.css";

const GlobalStyle = createGlobalStyle`
@import url('https://fonts.googleapis.com/css2?family=Source+Sans+Pro:wght@300;400&display=swap');
    html,
    body,
    div,
    span,
    applet,
    object,
    iframe,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    p,
    blockquote,
    pre,
    a,
    abbr,
    acronym,
    address,
    big,
    cite,
    code,
    del,
    dfn,
    em,
    img,
    ins,
    kbd,
    q,
    s,
    samp,
    small,
    strike,
    strong,
    sub,
    sup,
    tt,
    var,
    b,
    u,
    i,
    center,
    dl,
    dt,
    dd,
    menu,
    ol,
    ul,
    li,
    fieldset,
    form,
    label,
    legend,
    table,
    caption,
    tbody,
    tfoot,
    thead,
    tr,
    th,
    td,
    article,
    aside,
    canvas,
    details,
    embed,
    figure,
    figcaption,
    footer,
    header,
    hgroup,
    main,
    menu,
    nav,
    output,
    ruby,
    section,
    summary,
    time,
    mark,
    audio,
    video {
        margin: 0;
        padding: 0;
        border: 0;
        font-size: 100%;
        font: inherit;
        vertical-align: baseline;
    }
    /* HTML5 display-role reset for older browsers */
    article,
    aside,
    details,
    figcaption,
    figure,
    footer,
    header,
    hgroup,
    main,
    menu,
    nav,
    section {
        display: block;
    }
    /* HTML5 hidden-attribute fix for newer browsers */
    *[hidden] {
        display: none;
    }
    body {
        line-height: 1;
        font-family: 'Source Sans Pro', sans-serif;


    }
    menu,
    ol,
    ul {
        list-style: none;
    }
    blockquote,
    q {
        quotes: none;
    }
    blockquote:before,
    blockquote:after,
    q:before,
    q:after {
        content: "";
        content: none;
    }
    table {
        border-collapse: collapse;
        border-spacing: 0;
    }
`;

const darkTheme = {
    bgColor: "rgb(50,50,50)",
    textColor: "white",
    accentColor: "#44bd32",
    liColor: "#fff",
};

const lightTheme = {
    bgColor: "white",
    textColor: "black",
    accentColor: "#44bd32",
    liColor: "#555",
};

function App() {
    const [darkmode, setDarkmode] = useState(false); // 초기값을 false로 설정

    const toggleDarkMode = () => {
        setDarkmode((prev) => !prev); // 기존 상태를 반전
    };

    return (
        <ThemeProvider theme={darkmode ? darkTheme : lightTheme}>
            <GlobalStyle />
            <button onClick={toggleDarkMode}>{darkmode ? "Light Mode" : "Dark Mode"}</button>
            <Router />
        </ThemeProvider>
    );
}

export default App;
