import React from "react";
import { useForm } from "react-hook-form";
import { useSetRecoilState, useRecoilValue } from "recoil";
import { toDoState, IToDo, categoryState } from "./atoms";
import styled from "styled-components";

const Button = styled.button`
    font-size: 20px;
    margin: 10px 0;
`;

interface IForm {
    toDo: string;
}

function CreateToDo() {
    const setToDos = useSetRecoilState(toDoState);
    const myCategory = useRecoilValue(categoryState);
    const { handleSubmit, register } = useForm<IForm>();
    const handleValid = ({ toDo }: IForm) => {
        setToDos((prevToDos) => [{ text: toDo, id: Date.now(), category: myCategory }, ...prevToDos]);
        // setValue("toDo", "");
    };
    return (
        <form onSubmit={handleSubmit(handleValid)} style={{ display: "flex", flexDirection: "column" }}>
            <input
                {...register("toDo", {
                    required: "Please write a thing to do",
                })}
                placeholder="Write a to do"
            />

            <Button>Add</Button>
        </form>
    );
}

export default CreateToDo;
