// https://api.coinpaprika.com/v1/tickers
interface ICoinsProps {}

function Coins() {
    const setterFn = useSetRecoilState(isDarkAtom);

    const [coins, setCoins] = useState<CoinInterface[]>([]);
    useEffect(() => {
        fetch("https://api.coinpaprika.com/v1/coins")
            .then((response) => response.json())
            .then((data) => setCoins(data.slice(0, 100)));
    }, []);

    console.log(coins);
    return (
        <Container>
            <Header>
                <Title>Title</Title>
                <button>Toggle</button>
            </Header>

            <CoinsList>
                {coins.map((each) => (
                    <Coin key={each.id}>
                        <Link to={`/${each.id}`} state={{ name: each.name }}>
                            <Img src={`https://static.coinpaprika.com/coin/${each.id}/logo.png`} alt="symbol" />
                            {each.name} &rarr;
                        </Link>
                    </Coin>
                ))}
            </CoinsList>
        </Container>
    );
}

export default Coins;
