import styled from "styled-components";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";

const Container = styled.div`
    padding: 0px 20px;
    max-width: 480px;
    margin: 0 auto;
`;

const Header = styled.header`
    height: 10vh;
    display: flex;
    justify-content: center;
    align-items: center;
`;

const CoinsList = styled.ul``;
const Coin = styled.li`
    background-color: white;
    color: ${(props) => props.theme.bgColor};
    margin-bottom: 10px;
    padding: 20px;
    border-radius: 15px;
    a {
        display: block;
        transition: color 0.3s ease;
    }
    &:hover {
        a {
            color: ${(props) => props.theme.accentColor};
        }
    }
`;

const Title = styled.h1`
    color: ${(props) => props.theme.accentColor};
    font-size: 30px;
`;

//from https://api.coingecko.com/api/v3/coins/markets

interface CoinInterface {
    id: string;
    name: string;
    symbol: string;
    rank: number;
    is_new: boolean;
    is_active: boolean;
    type: string;
}
function Coins() {
    const [coins, setCoins] = useState<CoinInterface[]>([]);
    useEffect(() => {
        fetch("https://api.coingecko.com/v1/coins")
            .then((response) => response.json())
            .then((data) => setCoins(data));
    }, []);
    return (
        <Container>
            <Header>
                <Title>Title</Title>
            </Header>
            <CoinsList>
                {coins.map((each) => (
                    <Coin key={each.id}>
                        <Link to={`/${each.id}`}>{each.name}</Link>
                    </Coin>
                ))}
            </CoinsList>
        </Container>
    );
}

export default Coins;
