import React from "react";
import { useForm } from "react-hook-form";
import { useSetRecoilState, useRecoilValue } from "recoil";
import { toDoState, IToDo, categoryState } from "./atoms";
import styled from "styled-components";

const Button = styled.button`
    font-size: 20px;
    margin: 10px 0;
    padding: 5px;
    border: 1px solid #999;
    border-radius: 20px;
`;

const Input = styled.input`
    border: none;
    background: #f1f1f1;
    padding: 10px;
    font-size: 20px;
`;

interface IForm {
    toDo: string;
}

function CreateToDo() {
    const setToDos = useSetRecoilState(toDoState); // modi 함수
    const myCategory = useRecoilValue(categoryState); // value
    const { handleSubmit, register, setValue } = useForm<IForm>();

    const handleValid = ({ toDo }: IForm) => {
        // haddleValid - data  형태 : {toDo: 'df'}

        //useSetRecoilState 훅을 통해 상태를 업데이트하는 함수, 상태의 구조를 자유롭게 정의할 수 있다.
        setToDos((prevToDos) => [{ text: toDo, id: Date.now(), category: myCategory }, ...prevToDos]);

        setValue("toDo", "");
        //setValue
    };
    return (
        <form onSubmit={handleSubmit(handleValid)} style={{ display: "flex", flexDirection: "column" }}>
            <Input
                {...register("toDo", {
                    required: "Please write a thing to do",
                })}
                placeholder="Please write a thing to do."
                autoFocus
            />

            <Button>Add</Button>
        </form>
    );
}

export default CreateToDo;
