import styled from "styled-components";

import "./App.css";

const Box = styled.div`
    width: 100px;
    height: 100px;
    background-color: ${(props) => props.bgTest};
    display: flex;
    justify-content: center;
    align-items: center;

    //컴포넌트 안에 있는 일반 태그 선택 가능
    span {
        font-size: 36px;
        &:hover {
            font-size: 50px;
        }
    }
    span:hover {
        font-size: 50px;
    }
`;

function App() {
    return (
        <div>
            <Box>
                <span bgTest="green">T★☻</span>
            </Box>
        </div>
    );
}

export default App;
