import { atom, selector } from "recoil";

export interface IToDo {
    text: string;
    id: number;
    category: "TO_DO" | "DOING" | "DONE";
}

export const categoryState = atom({
    key: "category",
    default: "TO_DO",
});

export const toDoState = atom<IToDo[]>({
    key: "toDo",
    default: [],
});

//atom은 배열을 만들어줄 뿐이고
//selector는 atom을 받아서 output을 변형시킨다. ----derived state

export const toDoSelector = selector({
    key: "toDoSelector",
    get: ({ get }) => {
        const toDos = get(toDoState);
        const category = get(categoryState);

        if (category === "TO_DO") return toDos.filter((toDo) => toDo.category === "TO_DO");
        if (category === "DOING") return toDos.filter((toDo) => toDo.category === "DOING");
        if (category === "DONE") return toDos.filter((toDo) => toDo.category === "DOING");

        // return [toDos.filter((toDo) => toDo.category === "TO_DO"), toDos.filter((toDo) => toDo.category === "DOING"), toDos.filter((toDo) => toDo.category === "DONE")];
    },
});
