    };

    return (
        <li>
            <span>{text}</span>

            {category !== "DOING" && (
                <button name="DOING" onClick={onClickT}>
                    Doing
                </button>
            )}
            {category !== "TO_DO" && (
                <button name="TO_DO" onClick={onClickT}>
                    To Do
                </button>
            )}
            {category !== "DONE" && (
                <button name="DONE" onClick={onClickT}>
                    Done
                </button>
            )}
        </li>
    );
}

export default ToDo;
