import React from "react";
import { IToDo, toDoState, Categories } from "./atoms";
import { useSetRecoilState } from "recoil";
import styled from "styled-components";

const Li = styled.li`
    display: flex;
    justify-content: space-between;
    margin: 20px;
    border-bottom: 1px solid red;
    padding-bottom: 10px;
`;
const Button_Doing = styled.button`
    font-size: 20px;
`;

function ToDo({ text, category, id }: IToDo) {
    const setToDos = useSetRecoilState(toDoState);

    //1. 현재 버튼의 카테고리 추출
    const onClickT = (event: React.MouseEvent<HTMLButtonElement>) => {
        const {
            currentTarget: { name },
        } = event;

        console.log(name);
        // 2. 현재 클릭한 버튼의 index 추출

        setToDos((oldToDos) => {
            const targetIndex = oldToDos.findIndex((each) => each.id === id);

            const oldToDo = oldToDos[targetIndex];

            //new에서 바뀌는 것은 category, 나머지는 그대로 유지
            const newToDo = { text: text, id: id, category: name as any };

            const front = oldToDos.slice(0, targetIndex);
            const back = oldToDos.slice(targetIndex + 1);

            const finalToDos = [...front, newToDo, ...back];
            return finalToDos;
        });
    };

    return (
        <Li>
            <span>{text}</span>
            <span>
                {category !== Categories.DOING && (
                    <Button style={{ color: "#f19a39" }} name={Categories.DOING + ""} onClick={onClickT}>
                        Doing
                    </Button>
                )}
                {category !== Categories.TO_DO && (
                    <Button name={Categories.TO_DO + ""} onClick={onClickT}>
                        To Do
                    </Button>
                )}
                {category !== Categories.DONE && (
                    <Button style={{ color: "#67c467" }} name={Categories.DONE + ""} onClick={onClickT}>
                        Done
                    </Button>
                )}
            </span>
        </Li>
    );
}

export default ToDo;
